package com.saraya.service;

import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;

import com.saraya.model.Watch;

public class WatchService {

	private static ArrayList<Watch> watches=new ArrayList<Watch>();
	private static int count=4;
	
	static {
		watches.add(new Watch(1,2019,"Casio","G-Shock"," data:image/jpeg;base64,"));
		watches.add(new Watch(2,2021,"Rolex","GMT-MASTER II","https://content.rolex.com/dam/2021/upright-bba-with-shadow/m126711chnr-0002.png?impolicy=v6-upright&imwidth=270 "));
		watches.add(new Watch(3,2015,"Roger Dubuis","Excalibur Skeleton"," https://k8q7r7a2.stackpathcdn.com/wp-content/uploads/2014/12/Roger-Dubuis-Excalibur-Automatic-Skeleton-1.jpg"));
		watches.add(new Watch(4,2020,"Swatch","SUOK129 Unisex","https://www.gioielliallamoda.it/image/cache/data/Prodotti/swatch/suok129-swatch-800x800.jpg "));
		
	}
	
	public List<Watch> getAllWatches(){
		return watches;
	}
	public void addWatch(int year, String brand, String model,String image) {
		watches.add(new Watch(++count,year,brand,model,image));
	}
	public void deleteWatch(int id) {
		Iterator<Watch> iterate=watches.iterator();
		while(iterate.hasNext()) {
			Watch watch=iterate.next();
			if(watch.getId()==id) {
				iterate.remove();
			}
		}
	
	}
	
}
