package com.saraya.service;

import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;

import com.saraya.model.Yacht;

public class YachtService {

	private static ArrayList<Yacht> yachts=new ArrayList<Yacht>();
	private static int count=4;
	
	static {
		yachts.add(new Yacht(1,2019,"Oceanco ","My-esquel",(float)105.0,"https://www.yacht-zoo.com/wp-content/uploads/2019/11/my-esquel-oceanco-6-400x236.jpg"));
		yachts.add(new Yacht(2,2021,"Mangusta ","SuperYacht",(float)108.0,"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRHTW0uvGbDWnBX6xzbm7nCAU1WTmcviLIH4GPeYqxo8GJVNUPoxvjgzaJ1xJYDf1y1G1Y&usqp=CAU"));
		yachts.add(new Yacht(3,2022,"Avanti","Lurssen ",(float)201.0,"https://moranyachts.imgix.net/wp-content/uploads/lurssen-avanti-yacht-sale-moran-profile-2-scaled.jpg?auto=format&fit=crop&fm=webp%26nbsp%3B1x&h=1000&ixlib=php-3.1.0&w=1800&s=72cd4ef611b068ae8ee678e40f83a9b6"));
		yachts.add(new Yacht(4,2020,"Azimut","Grande",(float)150.0,"data:image/jpeg;base64,"));
		
	}
	
	public List<Yacht> getAllYachts(){
		return yachts;
	}
	public void addYacht(int year, String make, String model,float length,String image) {
		yachts.add(new Yacht(++count,year,make,model,length,image));
	}
	public void deleteYacht(int id) {
		Iterator<Yacht> iterate=yachts.iterator();
		while(iterate.hasNext()) {
			Yacht yacht=iterate.next();
			if(yacht.getId()==id) {
				iterate.remove();
			}
		}
	
	}
}
